function rect=drawcaltargetSM(el, x, y)% draw simple calibration target%% USAGE: rect=drawcaltarget(el, x, y)%%		el: eyelink default values%		x,y: position at which it should be drawn%		rect: % simple, standard eyelink versionsize=el.calibrationtargetsize;rect=[ x-size y-size x+size+1 y+size+1];%SCREEN(windowPtr,'FrameOval',[color],[rect],[penWidth],[penHeight],[penMode])Screen( el.window, 'FrameOval', el.foregroundcolour,  rect, el.calibrationtargetwidth, el.calibrationtargetwidth );Screen(el.window,'Flip');